// CSS Module declarations for Nativewind
declare module '*.css' {
  const content: any;
  export default content;
}

// Additional declarations for asset imports
declare module '*.png';
declare module '*.jpg';
declare module '*.jpeg';
declare module '*.gif';
declare module '*.svg';
declare module '*.webp';

// JSON module declarations
declare module '*.json' {
  const content: any;
  export default content;
}
